import React, { useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

function Marvel() {

  const {id} = useParams();

  const [item,setItem] = useState();
  const [comicsitem,setComics] = useState();

  const fetch = async () => {
    const res = await axios.get(`http://gateway.marvel.com/v1/public/characters/${id}?ts=1&apikey=50619cbe09281a68c45acb0b4bc1aa0e&hash=132b1915e343b43728d3de5e4775f843`); 
    setItem(res.data.data.results[0])
  }
  fetch();

  const comics = async () => {
    const comicsdata = await axios.get(`https://gateway.marvel.com/v1/public/characters/$%7Bid%7D/comics?ts=1&apikey=50619cbe09281a68c45acb0b4bc1aa0e&hash=132b1915e343b43728d3de5e4775f843`); 
    setItem(comicsdata.data.data.results[0])
  }
  comics();

  return (
    <>
      {
        (!item) ? <p>Veriler YÃ¼kleniyor</p> : (
          <div className="box-content">
          
            <div className="right-box">
              <img src={`${item.thumbnail.path}.${item.thumbnail.extension}`} alt="" />
            </div>
            <div className="left-box">
              <h1>{item.name}</h1>
              <h4>{item.description}</h4>
              <h4>{comicsitem.results.title}</h4>
              <h4></h4>
            </div>
          </div>
        ) 
      }
    </>
  )
}

export default Marvel;
