import Card  from "./Card"
import axios from "axios"



function Main() {

  const [url,setUrl] = useState("http://gateway.marvel.com/v1/public/characters?ts=1&apikey=50619cbe09281a68c45acb0b4bc1aa0e&hash=132b1915e343b43728d3de5e4775f843")

  const [item,setItem] = useState();

  useEffect(() => {
    const fetch = async () => {
      const res = await axios.get(url)
      setItem(res.data.data.results);
    }
    fetch();
  },[url])
  
   

  return (
    <>
      <div className="header">
        <div className="bg">
          <img src="./Images/bg.jpg" alt="" />
        </div>
        <div className="search-bar" >
          <img src="Images/logo.png" alt="logo" />
          <input type="search" placeholder='Search Hero' className='search' />
        </div>
      </div>
      <div className="content">
        
        {
          (!item) ? <p>YÃ¼kleniyor...</p> : <Card data={item} />
        }
      </div>
    </>
  )
}

export default Main
